import React, {useState} from 'react'

import classes from './todoForm.module.css'

const TodoForm = () => {
    const [input, setInput] = useState('')
    const [buttonActive, setButtonActive] = useState(false)

   
    const typingInputHandler = (event) => {
        setInput(event.target.value)
    }

    if(input.trim() !== ''){
        setButtonActive(true)
    }

    let buttonClass = buttonActive ? 'active' : ''

    const submitFormHandler = (event) => {
        event.preventDefault();

        console.log(input);
        setInput('')
        setButtonActive(false)
    }
    return (
        <form className={classes.inputField} onSubmit={submitFormHandler}>
            <input 
            type="text" 
            placeholder="Add your new todo" 
            onChange={typingInputHandler}
            value={input}
            />
            <button className={buttonClass}><i class="fas fa-plus"></i></button>
        </form>
    )
}

export default TodoForm
